@Composable
fun AllDecksScreen(
    navController: NavController
) {
    val paddingValue = 10.dp


    Column(
        modifier = Modifier
            .fillMaxSize()
            .background(MaterialTheme.colorScheme.background)
            .padding(paddingValue)
        ,
        verticalArrangement = Arrangement.Bottom,
        horizontalAlignment = Alignment.Start
    ) {
        Row(
            modifier = Modifier.fillMaxWidth().weight(1f),
            verticalAlignment = Alignment.CenterVertically
        ) {
            PokeSprite(pokeResId = R.drawable.absol, modifier = Modifier.weight(1f))
            PokeSprite(pokeResId = R.drawable.mincinno, modifier = Modifier.weight(1f))
            PokeSprite(pokeResId = R.drawable.mudkip, modifier = Modifier.weight(1f))
        }

        ChoosableDeck(
            modifier = Modifier.padding(vertical = paddingValue),
            title = "1st Deck",
            totalCards = 0,
            onClick = { navController.navigate(Screen.DeckModify.route) },
            imageUrl = "https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/4f7705ec-8c49-4eed-a56e-c21f3985254c/dah43cy-a8e121cb-934a-40f6-97c7-fa2d77130dd5.png/v1/fill/w_1024,h_1420,strp/pokemon_card_backside_in_high_resolution_by_atomicmonkeytcg_dah43cy-fullview.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..9GzaYS7sd8RPY5FlHca09J9ZQZ9D9zI69Ru-BsbkLDA"
        )

        ChoosableDeck(
            modifier = Modifier.padding(vertical = paddingValue),
            title = "2nd Deck",
            totalCards = 0,
            onClick = { navController.navigate(Screen.DeckModify.route) },
            imageUrl = "https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/4f7705ec-8c49-4eed-a56e-c21f3985254c/dah43cy-a8e121cb-934a-40f6-97c7-fa2d77130dd5.png/v1/fill/w_1024,h_1420,strp/pokemon_card_backside_in_high_resolution_by_atomicmonkeytcg_dah43cy-fullview.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..9GzaYS7sd8RPY5FlHca09J9ZQZ9D9zI69Ru-BsbkLDA"
        )
        ChoosableDeck(
            modifier = Modifier.padding(vertical = paddingValue),
            title = "3rd Deck",
            totalCards = 0,
            onClick = { navController.navigate(Screen.DeckModify.route) },
            imageUrl = "https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/4f7705ec-8c49-4eed-a56e-c21f3985254c/dah43cy-a8e121cb-934a-40f6-97c7-fa2d77130dd5.png/v1/fill/w_1024,h_1420,strp/pokemon_card_backside_in_high_resolution_by_atomicmonkeytcg_dah43cy-fullview.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..9GzaYS7sd8RPY5FlHca09J9ZQZ9D9zI69Ru-BsbkLDA"
        )
